parameters.functionCode:
// Parse incoming alert data
const alerts = $input.item.json.alerts || [];
const parsedAlerts = [];

for (const alert of alerts) {
  if (alert.status === 'firing') {
    const parsed = {
      alertname: alert.labels.alertname,
      instance: alert.labels.instance,
      severity: alert.labels.severity,
      type: alert.labels.type,
      summary: alert.annotations.summary,
      description: alert.annotations.description,
      metric_value: alert.annotations.metric_value,
      startsAt: alert.startsAt,
      generatorURL: alert.generatorURL,
      timestamp: new Date().toISOString()
    };
    parsedAlerts.push(parsed);
  }
}

if (parsedAlerts.length === 0) {
  return {
    json: {
      no_action: true,
      message: "All alerts resolved, no action needed"
    }
  };
}

const alert = parsedAlerts[0];

return {
  json: {
    alert_data: alert,
    ai_prompt: `You are a DevOps AI assistant analyzing infrastructure incidents.

Incident Details:
- Alert: ${alert.alertname}
- Type: ${alert.type}
- Instance: ${alert.instance}
- Severity: ${alert.severity}
- Current Value: ${alert.metric_value}
- Description: ${alert.description}
- Time: ${alert.timestamp}

Analyze this incident and respond ONLY with a JSON object (no markdown, no extra text):
{
  "spike_type": "temporary" or "consistent",
  "trend_prediction": "increasing" or "stable" or "decreasing",
  "scaling_needed": true or false,
  "confidence_level": "high" or "medium" or "low",
  "recommended_action": "auto_scale" or "manual_review" or "no_action",
  "reasoning": "Brief explanation",
  "estimated_duration": "Duration or 'ongoing'"
}`
  }
};

// Parse Gemini response
const geminiResponse = $input.item.json;
const alertData = $('Parse Alert Data').item.json.alert_data;

let analysis;
try {
  const responseText = geminiResponse.candidates[0].content.parts[0].text;
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in AI response');
  }
} catch (error) {
  console.error('Failed to parse AI response:', error);
  analysis = {
    spike_type: 'unknown',
    trend_prediction: 'unknown',
    scaling_needed: true,
    confidence_level: 'low',
    recommended_action: 'manual_review',
    reasoning: 'Failed to parse AI response, defaulting to manual review',
    estimated_duration: 'unknown'
  };
}

return {
  json: {
    ...alertData,
    ai_analysis: analysis,
    requires_scaling: analysis.recommended_action === 'auto_scale',
    requires_manual_review: analysis.recommended_action === 'manual_review',
    no_action_needed: analysis.recommended_action === 'no_action'
  }
};

